css:
.keylabels {
    font-family: "SF Pro", sans-serif;
    font-weight: 500;
}

.keylabels i {
    font-style: normal;
}

.keylabel1 {
    color: rgba(0,0,0,0) !important;
}

.keylabels .normal {
    color: black !important;
}

.keylabel0, .fn {
    color: DarkBlue !important;
}

.keylabel2, .nav {
    color: DarkOrange !important;
}

.keylabel6, .pad {
    color: DarkGreen !important;
}

.keylabel10, .meta {
    color: DarkMagenta !important;
    margin-top: 1px;
}

.keylabel8, .hold {
    font-family: "SF Pro", sans-serif;
    font-weight: 800;
    color: DimGray !important;
}

.keylabel9, .keylabel10, .keylabel11 {
    font-size: 6px !important;
    overflow: visible;
}

.smaller {
    font-size: 0.5em !important;
    line-height: 100%;
}

.larger {
    font-size: 1.5em !important;
    line-height: 100%;
}

.keylabels p, .keylabels ol, .keylabels ul {
    font-weight: 400;
    line-height: 1.1;
}

ol, ul {
    padding-left: 20px;
}

p + ol, p + ul {
    margin-top: -8px;
}

li {
    margin-bottom: 3px;
}
